import React, { Component } from "react";
import {
  View,
  Text,
  TextInput,
  Image,
  TouchableOpacity,
  Platform,
  ScrollView,
  Dimensions,
} from "react-native";
import styles from "../../style/orderdetail";
import { connect } from "react-redux";
import { UpdateOrderQuantity } from "../../actions/orderActions";
import restart from "../../../assets/1024x1024.png";
import { FlatGrid, SectionGrid } from "react-native-super-grid";
import { Entypo } from "@expo/vector-icons";
import { Poppins_300Light } from "@expo-google-fonts/poppins";
import { isLoaded } from "expo-font";
import { LinearGradient } from "expo-linear-gradient";
const windowWidth = Dimensions.get("window").width;
// import ModalDropdown from 'react-native-modal-dropdown';

class ItemsDetail extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      unTouch: true,
      loading: true,
      editable: false,
      instruction: false,
    };
  }
  componentDidMount() {
    // console.log("item detail props", this.props.items?.items[0]?.iteminfo?.order_id);

    if (this.state.items.length == 0) {
      this.setState({ loading: false });
    }

    if (
      this.props.items &&
      this.props.items?.items &&
      this.props.items?.items?.length
    ) {
      console.log("*********************");
      console.log("this.props.items.items", this.props.items.items);
      console.log("*********************");
      this.setState({ items: this.props.items.items });
      this.setState({ loading: false });
      // setState(this.props.items.items);
      // setLoad(true);
    }
  }

  Capitalize(str) {
    return str.charAt(0).toUpperCase() + str.slice(1);
  }
  changeEdit = () => {
    this.setState({ editable: !this.state.editable });
    // console.log("inside...", this.state.items);
  };

  render() {
    let content = this.props.items && this.props.items.items;


    return (
      // <ScrollView style={styles.itemDetailContainer}>
        Platform.OS === "web" ? (


          <React.Fragment>

            <View>
              <View
                style={{
                  backgroundColor: "#000",
                  height: 20,
                  width: "100%",
                  shadowColor: "#000",
                  shadowOffset: {
                    width: 1,
                    height: 10,
                  },
                  shadowOpacity: 3,
                  shadowRadius: 10,

                  elevation: 5,
                }}
              ></View>
              <View style={{}}>
                {this.state.loading == false &&
                this.state.items &&
                this.state.items.length ? (
                  // this.state.items.map((i) => (
                  //   <Text style={{ color: "white", fontSize: 100 }}>
                  //     {i?.iteminfo?.order_id}
                  //   </Text>
                  // ))
                  <Text
                    style={{
                      color: "#67869B",
                      fontSize: 22,
                      fontWeight: "bold",
                      paddingHorizontal: 15,
                      fontFamily: "Poppins_700Bold",
                    }}
                  >
                    Order # {this.state.items[0].iteminfo.order_id}
                  </Text>
                ) : (
                  <Text>no id</Text>
                )}
              </View>

              <View
                // colors={["#22292C", "#68879C", "#68879C", "#22292C"]}
                // start={[-0.3, 0.01]}
                style={{
                  // overflow: "scroll",
                  paddingHorizontal: 15,
                  backgroundColor: "rgba(104, 135, 156, 0.4)",
                  borderRadius: 10,
                  marginHorizontal: 25,
                  marginTop: 20,
                  marginBottom: 10,
                  // borderWidth: 1,
                  // borderColor: "#50708b",
                  shadowColor: "#50708b",
                  shadowOffset: { width: 1, height: 1 },
                  shadowRadius: 10,
                }}
              >
                <View
                  style={{
                    alignSelf: "flex-end",
                    justifyContent: "center",
                    alignItems: "center",

                    flexDirection: "row",
                    // borderWidth: 1
                  }}
                >
                  <TouchableOpacity
                    style={{
                      flexDirection: "row",
                      alignItems: "center",
                      justifyContent: "center",
                      paddingRight: 30,
                      // borderWidth: 1
                    }}
                  >
                    <Image
                      style={{ width: 70, height: 70, resizeMode: "cover" }}
                      source={require("../../../assets/plus.png")}
                    />
                    <Text
                      style={{
                        fontFamily: "Poppins_800ExtraBold",
                        // fontWeight: "bold",
                        color: "white",
                        fontSize: 16,
                        textShadowColor: "#000",
                        textShadowOffset: { width: 1, height: 1 },
                        textShadowRadius: 5,

                        elevation: 5,
                      }}
                    >
                      Add Items
                    </Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    style={{
                      marginRight: 10,
                      borderWidth: 1,
                      borderStyle: "solid",
                      borderColor: "#fff",
                      borderRadius: 30,
                      justifyContent: "center",
                      alignItems: "center",
                      // backgroundColor: "#336599",
                      height: 30,
                      shadowColor: "#2bfac3",
                      shadowOffset: { width: 0, height: 0.5 },
                      shadowRadius: 8,
                      elevation: 5,
                      shadowOpacity: 0.5,
                      // box-shadow: rgb(43 250 195) 0px 1px 3px;
                    }}
                    onPress={() => this.props.updateBasicInfo()}
                  >
                    <Text
                      style={{
                        color: "white",
                        paddingHorizontal: 10,
                        fontWeight: "bold",
                      }}
                    >
                      Save & Close
                    </Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    onPress={() => this.props.updateBasicInfo()}
                    style={{
                      marginRight: 10,
                      borderWidth: 1,
                      borderStyle: "solid",
                      borderColor: "#fff",
                      borderRadius: 30,
                      justifyContent: "center",
                      alignItems: "center",
                      // backgroundColor: "#494848",
                      height: 30,
                      shadowColor: "#e853fa",
                      shadowOffset: { width: 0, height: 0.5 },
                      shadowRadius: 8,
                      elevation: 5,
                      shadowOpacity: 0.5,
                    }}
                  >
                    <Text
                      style={{
                        color: "white",
                        paddingHorizontal: 10,
                        fontWeight: "bold",
                      }}
                    >
                      Cancel
                    </Text>
                  </TouchableOpacity>
                </View>

                <View

                  style={{
                    overflow: "scroll",
                    flex: 1,
                    marginHorizontal: 10,
                    backgroundColor: "rgba(15, 39, 58, 0.4)",
                    borderRadius: 15,
                    marginBottom: 50,
                    // opacity: 0.5
                    // width: '100%'
                  }}
                >
                  <View
                    style={[styles.itemContainer, { paddingHorizontal: 10 }]}
                  >
                    <View style={[styles.itemView, { width: 50 }]}>
                      {/* <Entypo name='chevron-down' size={20} color='white' onPress={()=>{}} /> */}
                      {/* <ModalDropdown options={['option 1', 'option 2']} style={{width: 40, height: 30}} /> */}
                    </View>
                    <View
                      style={{
                        flexDirection: "row",
                        paddingBottom: 10,
                        width: "95%",
                        justifyContent: "space-around",
                      }}
                    >
                      <View
                        style={[
                          styles.itemView,
                          { width: 70, marginRight: 15 },
                        ]}
                      ></View>
                      <View style={[styles.itemView, { width: 400 }]}>
                        <Text
                          style={[
                            styles.gridHeader,
                            // {
                            //   fontWeight: "bold",
                            //   fontSize: 22,
                            //   color: "#ABB6BC",
                            // },
                          ]}
                        >
                          Product Name
                        </Text>
                      </View>
                      <View style={[styles.itemView, { width: 350 }]}>
                        <Text
                          style={[
                            styles.gridHeader,
                            // {
                            //   fontWeight: "bold",
                            //   fontSize: 22,
                            //   color: "#ABB6BC",
                            // },
                          ]}
                        >
                          SKU
                        </Text>
                      </View>
                      <View style={[styles.itemView, { width: 100 }]}>
                        <Text
                          style={[
                            styles.gridHeader,
                            // {
                            //   fontWeight: "bold",
                            //   fontSize: 22,
                            //   color: "#ABB6BC",
                            // },
                          ]}
                        >
                          Status
                        </Text>
                      </View>
                      <View style={[styles.itemView, { width: 350 }]}>
                        <Text
                          style={[
                            styles.gridHeader,
                            // {
                            //   fontWeight: "bold",
                            //   fontSize: 22,
                            //   color: "#ABB6BC",
                            // },
                          ]}
                        >
                          Barcode
                        </Text>
                      </View>
                      <View style={[styles.itemView, { width: 100 }]}>
                        <Text
                          style={[
                            styles.gridHeader,
                            // {
                            //   fontWeight: "bold",
                            //   fontSize: 22,
                            //   color: "#ABB6BC",
                            // },
                          ]}
                        >
                          QTY Ordered
                        </Text>
                      </View>
                      <View style={[styles.itemView, { width: 150 }]}>
                        <Text
                          style={[
                            styles.gridHeader,
                            // {
                            //   fontWeight: "bold",
                            //   fontSize: 22,
                            //   color: "#ABB6BC",
                            // },
                          ]}
                        >
                          Location l
                        </Text>
                      </View>
                      <View style={[styles.itemView, { width: 100 }]}>
                        <Text style={styles.gridHeader}>QOH</Text>
                      </View>
                      {this.state.instruction ? (
                        <View style={[styles.itemView, { width: 130 }]}>
                          <Text style={styles.gridHeader}>Available INV</Text>
                        </View>
                      ) : (
                        <View></View>
                      )}
                    </View>
                  </View>

                  <FlatGrid
                    itemDimension={windowWidth}
                    data={this.state.items}
                    style={styles.gridView}
                    renderItem={({ item }) => (
                      <View style={[styles.itemContainer]}>
                        <TouchableOpacity
                          onPress={() => {
                            this.props.removeIds.includes(item.iteminfo.id);
                          }}
                          style={[
                            styles.itemView,
                            { width: 50, marginHorizontal: 15 },
                          ]}
                        >
                          <Image
                            style={{
                              width: 70,
                              height: 70,
                              resizeMode: "stretch",
                            }}
                            source={require("../../../assets/cross.png")}
                          />
                        </TouchableOpacity>
                        <View
                          style={{
                            flexDirection: "row",
                            borderBottomWidth: 0.7,
                            borderColor: "rgba(250, 250, 250, .4)",
                            paddingVertical: 10,
                            width: "95%",
                            justifyContent: "space-around",
                          }}
                        >
                          <View
                            style={[
                              styles.itemView,
                              { width: 70, marginRight: 15 },
                            ]}
                          >
                            {/* <Text style={styles.itemName}>{item.productinfo.name}</Text> */}
                            <Image
                              style={{
                                width: 70,
                                height: 70,
                                borderRadius: 5,
                                resizeMode: "contain",
                              }}
                              source={{ uri: item.image }}
                            />
                          </View>
                          <View style={[styles.itemView, { width: 400 }]}>
                            <Text
                              style={[styles.itemName, { color: "#aad9fc" }]}
                            >
                              {item.productinfo.name}
                            </Text>
                          </View>
                          <View style={[styles.itemView, { width: 350 }]}>
                            <Text style={styles.itemName}>{item.sku}</Text>
                          </View>
                          <View style={[styles.itemView, { width: 100 }]}>
                            {item.productinfo.status === "active" ? (
                              <Text style={styles.statusActive}>
                                {this.Capitalize(item.productinfo.status)}
                              </Text>
                            ) : (
                              // <Text>{this.Capitalize(this.state.title)} </Text>
                              <Text style={styles.statusNew}>
                                {this.Capitalize(item.productinfo.status)}
                              </Text>
                            )}
                          </View>
                          <View style={[styles.itemView, { width: 350 }]}>
                            <Text style={styles.itemName}>{item.barcode}</Text>
                          </View>
                          <View
                            style={[
                              styles.itemView,
                              {
                                width: 100,
                                flexDirection: "row",
                                justifyContent: "center",
                                alignItems: "center",
                              },
                            ]}
                          >
                            {this.state.editable == true ? (
                              <TextInput
                                placeholder={item.iteminfo.qty}
                                onChangeText={() => {}}
                                style={{ width: 25, height: 25, fontSize: 19 }}
                              />
                            ) : (
                              <Text style={styles.itemName}>
                                {item.iteminfo.qty}
                              </Text>
                            )}
                            <TouchableOpacity
                              onPress={() => {
                                // this.setState({ editable: true });
                                this.changeEdit();
                              }}
                            >
                              <Image
                                style={{
                                  width: 20,
                                  height: 20,
                                  resizeMode: "contain",
                                }}
                                source={require("../../../assets/edit.png")}
                              />
                            </TouchableOpacity>
                          </View>
                          <View style={[styles.itemView, { width: 150 }]}>
                            <Text style={styles.itemName}>
                              {item.location_primary}
                            </Text>
                          </View>
                          <View style={[styles.itemView, { width: 100 }]}>
                            <Text style={styles.itemName}>
                              {item.qty_on_hand}
                            </Text>
                          </View>
                          {this.state.instruction ? (
                            <View style={[styles.itemView, { width: 130 }]}>
                              <Text style={styles.itemName}>
                                {item.available_inv}
                              </Text>
                            </View>
                          ) : (
                            <View></View>
                          )}
                        </View>
                      </View>
                    )}
                  />
                </View>
              </View>
              {/* </LinearGradient> */}
            </View>
          </React.Fragment>
        ) : (
          <React.Fragment>
            <View style={{ flex: 1, flexDirection: "row" }}>
              <View
                style={{
                  flex: 2,
                  flexDirection: "row",
                  justifyContent: "flex-start",
                  margin: 5,
                  marginLeft: 10,
                }}
              >
                <TouchableOpacity
                  onPress={() => this.props.addOrderItem(items)}
                  style={{
                    borderWidth: 1,
                    borderStyle: "solid",
                    borderColor: "#336597",
                    backgroundColor: "#336599",
                    justifyContent: "center",
                  }}
                >
                  <Text
                    style={{
                      color: "white",
                      padding: 4,
                      fontSize: 13,
                      fontWeight: "bold",
                    }}
                  >
                    Add Item
                  </Text>
                </TouchableOpacity>
                <TouchableOpacity
                  onPress={() => this.props.removeItems()}
                  style={
                    this.props.removeIds && this.props.removeIds.length > 0
                      ? {
                          marginRight: 2,
                          borderWidth: 1,
                          borderStyle: "solid",
                          borderColor: "#336597",
                          backgroundColor: "#961F1F",
                          justifyContent: "center",
                        }
                      : {
                          marginRight: 2,
                          borderWidth: 1,
                          borderStyle: "solid",
                          borderColor: "#336597",
                          backgroundColor: "#c48787",
                          justifyContent: "center",
                        }
                  }
                >
                  <Text
                    style={{
                      color: "white",
                      padding: 4,
                      fontSize: 13,
                      fontWeight: "bold",
                    }}
                  >
                    Remove selected
                  </Text>
                </TouchableOpacity>
              </View>
              <View
                style={{
                  flex: 2,
                  flexDirection: "row",
                  justifyContent: "flex-end",
                  margin: 5,
                }}
              >
                <TouchableOpacity
                  onPress={() => this.props.updateBasicInfo()}
                  style={{
                    borderWidth: 1,
                    borderStyle: "solid",
                    borderColor: "#336597",
                    backgroundColor: "#494848",
                    justifyContent: "center",
                  }}
                >
                  <Text
                    style={{
                      color: "white",
                      padding: 4,
                      fontSize: 13,
                      fontWeight: "bold",
                    }}
                  >
                    Cancel
                  </Text>
                </TouchableOpacity>
                <TouchableOpacity
                  style={{
                    marginRight: 10,
                    borderWidth: 1,
                    borderStyle: "solid",
                    borderColor: "#336597",
                    backgroundColor: "#336599",
                    justifyContent: "center",
                  }}
                  onPress={() => this.props.updateBasicInfo()}
                >
                  <Text
                    style={{
                      color: "white",
                      padding: 4,
                      fontSize: 13,
                      fontWeight: "bold",
                    }}
                  >
                    Save & Close
                  </Text>
                </TouchableOpacity>
              </View>
            </View>
            {items &&
              items.length > 0 &&
              items.map((item, index) => {
                return (
                  <View
                    key={index}
                    style={
                      this.props.removeIds.includes(item.iteminfo.id) && {
                        backgroundColor: "#799cbf",
                      }
                    }
                  >
                    <View
                      style={{
                        flex: 1,
                        flexDirection: "row",
                        borderWidth: 1,
                        borderColor: "#000",
                        borderRadius: 10,
                        width: "96%",
                        marginLeft: "auto",
                        marginRight: "auto",
                        padding: 5,
                      }}
                    >
                      <View style={{ width: "20%" }}>
                        <TouchableOpacity
                          onPress={() => this.props.removeOrderItem(item)}
                        >
                          <Image
                            style={{
                              position: "relative",
                              width: "100%",
                              height: "100%",
                              resizeMode: "contain",
                              marginBottom: 2,
                            }}
                            source={{
                              uri: item.image !== null ? item.image : "",
                            }}
                          />
                        </TouchableOpacity>
                      </View>
                      <View style={{ width: "80%" }}>
                        <View
                          style={{
                            flex: 1,
                            flexDirection: "row",
                            alignItem: "center",
                          }}
                        >
                          <View style={{ flex: 2, flexDirection: "row" }}>
                            <Text style={{ padding: 5 }}>QTY</Text>
                            <TextInput
                              keyboardType="numeric"
                              style={{
                                borderWidth: 1,
                                borderRadius: 10,
                                width: 50,
                                paddingLeft: 10,
                                paddingRight: 5,
                              }}
                              value={
                                item.iteminfo.qty &&
                                JSON.stringify(item.iteminfo.qty)
                              }
                              onChangeText={(text) =>
                                this.props.handleChange(text, item)
                              }
                              onBlur={() => this.props.updateQuantity(item)}
                            />
                          </View>
                          <View style={{ flex: 4, justifyContent: "center" }}>
                            <TouchableOpacity
                              style={styles.value}
                              onPress={() => this.props.removeOrderItem(item)}
                            >
                              <Text numberOfLines={1} ellipsizeMode="tail">
                                {" "}
                                {item.productinfo.name}{" "}
                              </Text>
                            </TouchableOpacity>
                          </View>
                        </View>

                        <TouchableOpacity
                          onPress={() => this.props.removeOrderItem(item)}
                        >
                          <View
                            style={{
                              flex: 1,
                              flexDirection: "row",
                              alignItem: "center",
                            }}
                          >
                            <View style={{ flex: 2, flexDirection: "row" }}>
                              <Text
                                numberOfLines={1}
                                ellipsizeMode="tail"
                                style={{ padding: 5 }}
                              >
                                {item.sku}
                              </Text>
                            </View>
                            <View style={{ flex: 2, flexDirection: "row" }}>
                              {item.productinfo.custom_product_1 !== "" && (
                                <Text
                                  numberOfLines={1}
                                  ellipsizeMode="tail"
                                  style={{ padding: 5 }}
                                >
                                  C1: {item.productinfo.custom_product_1}
                                </Text>
                              )}
                            </View>
                            <View style={{ flex: 4, justifyContent: "center" }}>
                              <Text numberOfLines={1} ellipsizeMode="tail">
                                {" "}
                                {item.barcode}{" "}
                              </Text>
                            </View>
                          </View>
                        </TouchableOpacity>
                      </View>
                    </View>
                  </View>
                );
              })}
          </React.Fragment>
        )
      // </ScrollView>
    );
  }
}

