                                                              ellipsizeMode='tail'
                                                            >
                                                              {
                                                                item &&
                                                                item.barcodes.length > 0 &&
                                                                item.barcodes[0].barcode
                                                              }
                                                            </Text>
                                                          </View>
                                                        </View>

