                                  </Text>
                                </View>
                              </View>
                              <View style={{ backgroundColor: "#666666" }}>
                                {
                                  Order.unscanned_items &&
                                  Order.unscanned_items[0] &&
                                  Order.unscanned_items[0].location2 !== null &&
                                  Order.unscanned_items[0].location2 !== "" &&
