                              <Text style={{ fontSize: 18, fontWeight: "bold", color: "#fff", padding: 4 }}>SCAN LOG</Text>
                            </TouchableOpacity>
                          </View>
                        </View>
                        <ScrollView>
                          {
                            this.state.scannedList &&
                            <React.Fragment>
                              {
                                Order.unscanned_items && Order.unscanned_items.length > 0
