                                                    ellipsizeMode='tail'
                                                    style={item.name && item.name.length <= 32 ? { fontSize: 20, width: "75%" } : { fontSize: 16, width: "75%" }}>
                                                    {item.name}
                                                  </Text>
                                                </TouchableOpacity>
                                                <Text numberOfLines={1}
                                                  ellipsizeMode='tail'
                                                  style={{ color: "#fff", fontSize: 20, fontWeight: "600", width: "75%" }}
